mapper.select:
SELECT * FROM BOARD
UPPER(B_TITLE) LIKE CONCAT('%', #{b_title}, '%')
LIMIT 5 OFFSET #{start}

mapper.insert:
INSERT INTO BOARD
	(  B_TITLE
	  , B_WRITER
	  , B_CONTENT
	  , B_DATE
	  , DELT_YN
	 )
VALUES ( #{b_title}
	  , #{b_writer}
	  , #{b_content}
	  , NOW()
	  , 'N'
	  )

mapper.update:
			UPDATE BOARD
			SET  B_TITLE=#{b_title}
				, B_WRITER=#{b_writer}
				, B_CONTENT=#{b_content}
				, B_DATE = NOW()
			 WHERE B_NO=#{b_no}


UPDATE BOARD
SET DELT_YN ='Y'
WHERE B_NO=#{b_no}

mapper.select:
SELECT * FROM BOARDBACKUP
UPPER(B_NO) LIKE CONCAT(#{b_no})
ORDER BY LOGNUM LIMIT 5 OFFSET #{start}
